{"compiler":{"version":"0.8.7+commit.e28d00a7"},"language":"Solidity","output":{"abi":[{"inputs":[],"stateMutability":"nonpayable","type":"constructor"},{"inputs":[],"name":"CHEST_FEE_PERCENTAGE","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"MAX_DEPOSIT_AMOUNT","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"MIN_DEPOSIT_AMOUNT","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"chest","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"currentRound","outputs":[{"internalType":"uint256","name":"capacity","type":"uint256"},{"internalType":"uint256","name":"depositedAmount","type":"uint256"},{"internalType":"address","name":"winner","type":"address"},{"internalType":"bool","name":"isActive","type":"bool"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"deposit","outputs":[],"stateMutability":"payable","type":"function"},{"inputs":[],"name":"owner","outputs":[{"internalType":"address","name":"","type":"address"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"uint256","name":"capacity","type":"uint256"}],"name":"startNewRound","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[],"name":"withdrawChest","outputs":[],"stateMutability":"nonpayable","type":"function"}],"devdoc":{"kind":"dev","methods":{},"version":1},"userdoc":{"kind":"user","methods":{},"version":1}},"settings":{"compilationTarget":{"contracts/CasinoContract.sol":"Casino"},"evmVersion":"london","libraries":{},"metadata":{"bytecodeHash":"ipfs","useLiteralContent":true},"optimizer":{"enabled":true,"runs":200},"remappings":[]},"sources":{"contracts/CasinoContract.sol":{"content":"pragma solidity ^0.8.7;

contract Casino {
    address public owner;
    uint256 public chest;
    uint256 public constant CHEST_FEE_PERCENTAGE = 20;
    uint256 public constant MIN_DEPOSIT_AMOUNT = 10 ether;
    uint256 public constant MAX_DEPOSIT_AMOUNT = 100 ether;

    struct Round {
        uint256 capacity;
        uint256 depositedAmount;
        address winner;
        bool isActive;
    }

    Round public currentRound;

    constructor() {
        owner = msg.sender;
        chest = 0;
        currentRound.isActive = false;
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "Only owner can call this function");
        _;
    }

    function startNewRound(uint256 capacity) public onlyOwner {
        require(!currentRound.isActive, "Current round is still active");
        require(capacity > 0, "Capacity should be greater than 0");

        currentRound.capacity = capacity;
        currentRound.depositedAmount = 0;
        currentRound.isActive = true;
        currentRound.winner = address(0);
    }

    function deposit() public payable {
        require(currentRound.isActive, "No active round at the moment");
        require(currentRound.depositedAmount < currentRound.capacity, "Round capacity already reached");
        require(msg.value >= MIN_DEPOSIT_AMOUNT && msg.value <= MAX_DEPOSIT_AMOUNT, "Deposit amount should be between min and max");

        currentRound.depositedAmount += msg.value;
        if (currentRound.depositedAmount == currentRound.capacity) {
            currentRound.isActive = false;
            currentRound.winner = msg.sender;
            uint256 winnerAmount = (currentRound.depositedAmount * (100 - CHEST_FEE_PERCENTAGE)) / 100;
            uint256 chestAmount = currentRound.depositedAmount - winnerAmount;
            payable(msg.sender).transfer(winnerAmount);
            chest += chestAmount;
        }
    }

    function withdrawChest() public onlyOwner {
        require(chest > 0, "No chips in the chest");
        uint256 amount = chest;
        chest = 0;
        payable(owner).transfer(amount);
    }
}","keccak256":"0x286f131a6014b8351983d1e1ac74ab383dc4286643f08f5cbdc37428d6538cce"}},"version":1}

